sap.ui.define(["sap/ui/core/mvc/Controller"], function (Controller) {
	"use strict";
	return Controller.extend("com.nav1.nav1.controller.View1", {
		onInit: function () {},
		/**
		 *@memberOf com.nav1.nav1.controller.View1
		 */
		onPress: function (oEvent) {
			//This code was generated by the layout editor.
			/*eslint-disable no-alert*/
			 alert("Detalle de " + oEvent.getSource().getBindingContext());
			 /*eslint-enable no-alert*/
			
			var rut = oEvent.getSource().getBindingContext().getProperty("Rut");
			 
			// Now Get the Router Info
			var oRouter = sap.ui.core.UIComponent.getRouterFor(this);
			 
			// Llamar al target
			oRouter.navTo("detalle", { rutPath: rut});
		}
	});
});